import React, { useState } from "react";
import { Modal, Button } from "react-bootstrap";

import colors from "../../config/colors";

function SendMoneyModal(props) {
  const [detail, setDetail] = useState({
    accountNo: "",
    name: "",
    amount: 0,
    message: "",
  });
  return (
    <Modal {...props} aria-labelledby="contained-modal-title-vcenter" centered>
      <Modal.Header closeButton>
        <Modal.Title
          id="contained-modal-title-vcenter"
          className="w-100 text-center title"
        >
          <span style={{ fontSize: "0.8em", color: colors.primary2 }}>
            {props.accountno}
          </span>
          &nbsp;&nbsp;[
          <span style={{ fontSize: "0.8em", color: colors.secondary1 }}>
            {props.name}
          </span>
          ]
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <div>
          <label
            htmlFor="account-no"
            className="title2"
            style={{ width: "35%" }}
          >
            Account No :
          </label>
          <input
            type="text"
            value={detail.accountno}
            placeholder="Receiver's Account no"
            className="input-field"
            style={{ width: "60%" }}
            onChange={(event) =>
              setDetail((prevState) => {
                return { ...prevState, accountNo: event.target.value };
              })
            }
          />
          <label htmlFor="name" className="title2" style={{ width: "35%" }}>
            Full Name :
          </label>
          <input
            type="text"
            value={detail.name}
            placeholder="Account Holder Name"
            className="input-field"
            style={{ width: "60%" }}
            onChange={(event) =>
              setDetail((prevState) => {
                return { ...prevState, name: event.target.value };
              })
            }
          />
          <label htmlFor="name" className="title2" style={{ width: "35%" }}>
            Amount :
          </label>
          <input
            type="text"
            value={detail.amount}
            placeholder="Amount to send"
            className="input-field"
            style={{ width: "60%" }}
            onChange={(event) =>
              setDetail((prevState) => {
                return { ...prevState, amount: event.target.value };
              })
            }
          />
          <label htmlFor="name" className="title2" style={{ width: "35%" }}>
            Message :
          </label>
          <input
            type="text"
            value={detail.message}
            placeholder="Hi this is your birthday's gift."
            className="input-field"
            style={{ width: "60%" }}
            onChange={(event) =>
              setDetail((prevState) => {
                return { ...prevState, message: event.target.value };
              })
            }
          />
        </div>
      </Modal.Body>
      <Modal.Footer>
        <Button
          onClick={props.onHide}
          className="btn"
          style={{ backgroundColor: colors.primary2 }}
        >
          Cancel
        </Button>
        <Button>Send Money</Button>
      </Modal.Footer>
    </Modal>
  );
}

export default SendMoneyModal;
