import React, { useState } from "react";
import { Modal, Button } from "react-bootstrap";

import colors from "../../config/colors";

import "./Modal.css";

function ModalComponent(props) {
  const { user, show, accountNo, onHide } = props;
  // const [state, setState] = useState(user);
  // console.log("state:", state);
  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title
          id="contained-modal-title-vcenter"
          className="w-100 text-center title"
        >
          Update User Profile
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <div>
          <label htmlFor="account-no" className="title2">
            Account No :
          </label>
          <input
            type="text"
            id="account-no"
            name="Account No"
            value={accountNo}
            className="input-field"
            disabled
          />
          <label htmlFor="account-no" className="title2">
            Full Name :
          </label>
          <input
            type="text"
            id="account-no"
            name="Account No"
            value={accountNo}
            className="input-field"
            disabled
          />
        </div>
        <div>
          <label htmlFor="account-no" className="title2">
            Email Id :
          </label>
          <input
            type="text"
            id="account-no"
            name="Account No"
            value={accountNo}
            // onChange={(event) =>
            //   setState((prevState) => {
            //     return { ...prevState, emailId: event.target.value };
            //   })
            // }
            className="input-field"
            disabled
          />
          <label htmlFor="account-no" className="title2">
            Balance :
          </label>
          <input
            type="text"
            id="account-no"
            name="Account No"
            value={` $ ${accountNo}`}
            className="input-field"
            style={{ backgroundColor: colors.light3 }}
            disabled
          />
        </div>
      </Modal.Body>
      <Modal.Footer>
        <Button onClick={onHide}>Close</Button>
        <Button>Save Changes</Button>
      </Modal.Footer>
    </Modal>
  );
}

export default ModalComponent;
