import Axios from "axios";
import React, { useEffect, useState } from "react";
import { Modal, Button } from "react-bootstrap";

import colors from "../../config/colors";

import "./Modal.css";

function ModalComponent(props) {
  const [userDetail, setUserDetailed] = useState();
  console.log("props: ", props);

  useEffect(() => {
    Axios.get("http://localhost:5000/search/" + props.accountNo)
      .then((response) => setUserDetailed(response.data))
      .catch((e) => {
        console.log("Can't catch the response...", e);
      });
  }, [props.accountNo]);
  console.log("user Detail: ", userDetail);
  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title
          id="contained-modal-title-vcenter"
          className="w-100 text-center title"
        >
          Update User Profile
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <div>
          <label htmlFor="account-no" className="title2">
            Account No :
          </label>
          <input
            type="text"
            id="account-no"
            name="Account No"
            value={props.accountNo}
            className="input-field"
            disabled
          />
          <label htmlFor="account-no" className="title2">
            Full Name :
          </label>
          <input
            type="text"
            id="account-no"
            name="Account No"
            value={props.accountNo}
            className="input-field"
            disabled
          />
        </div>
        <div>
          <label htmlFor="account-no" className="title2">
            Email Id :
          </label>
          <input
            type="text"
            id="account-no"
            name="Account No"
            value={props.accountNo}
            // onChange={(event) =>
            //   setState((prevState) => {
            //     return { ...prevState, emailId: event.target.value };
            //   })
            // }
            className="input-field"
            disabled
          />
          <label htmlFor="account-no" className="title2">
            Balance :
          </label>
          <input
            type="text"
            id="account-no"
            name="Account No"
            value={` $ ${props.accountNo}`}
            className="input-field"
            style={{ backgroundColor: colors.light3 }}
            disabled
          />
        </div>
      </Modal.Body>
      <Modal.Footer>
        <Button onClick={props.onHide}>Close</Button>
        <Button>Save Changes</Button>
      </Modal.Footer>
    </Modal>
  );
}

export default ModalComponent;
