import React from "react";
import colors from "../../config/colors";

import "./Home.css";
import "../../App.css";

function TableRow(props) {
  const {
    srNo,
    accountNo,
    name,
    email,
    balance,
    contactNo,
    accountType,
    updateCallback,
    deleteCallback,
    sendMoneyCallback,
  } = props;

  return (
    <>
      <tr>
        <div onClick={() => sendMoneyCallback(accountNo, name)}>
          <th scope="row" className="text-center">
            {srNo}
          </th>
          <td className="text-center">{accountNo}</td>
          <td className="text-center">{name}</td>
          <td className="text-center">{email}</td>
          <td className="text-center">$ {balance}</td>
          <td className="text-center">{contactNo}</td>
          <td className="text-center">{accountType}</td>
        </div>
        <td className="text-center">
          <div
            className="btn text-light mr-2"
            style={{ backgroundColor: colors.secondary2 }}
            onClick={() => updateCallback(accountNo)}
          >
            Update
          </div>
          <div
            className="btn text-light"
            style={{ backgroundColor: colors.primary2 }}
            onClick={() => deleteCallback(accountNo)}
          >
            &nbsp;Delete
          </div>
        </td>
      </tr>
    </>
  );
}

export default TableRow;
