import React, { useEffect, useState } from "react";
import Axios from "axios";

import TransacTableRow from "./TransacTableRow";

function TransacTable(props) {
  const [state, setState] = useState([]);
  const [isGenerate, setIsGenerate] = useState(false);
  const [id, setId] = useState();

  const generateInvoice = (key) => {
    console.log("invoice: ", key);
  };

  const deleteTransac = (key) => {
    console.log("Delete: ", key);
  };

  useEffect(() => {
    Axios.get("http://localhost:5000/allTransacs")
      .then((response) => setState(response.data))
      .catch((e) => {
        console.log("Can't catch the response...", e);
      });
  }, []);

  return (
    <div className="table-responsive-xl">
      <table className="table">
        <thead className="thead-dark">
          <tr>
            <>
              <th scope="col" className="text-center">
                Sr.No
              </th>
              <th scope="col" className="text-center">
                Sender Account No
              </th>
              <th scope="col" className="text-center">
                Receiver Account No
              </th>
              <th scope="col" className="text-center">
                Transfer Amount
              </th>
              <th scope="col" className="text-center">
                Date
              </th>
            </>
            <th scope="col" className="text-center">
              Action
            </th>
          </tr>
        </thead>
        <tbody>
          {state.map((val, key) => {
            return (
              <TransacTableRow
                key={val._id}
                srNo={key + 1}
                id={val._id}
                sender={val.sender}
                senderAccountNo={val.senderAccountNo}
                receiver={val.receiver}
                receiverAccountNo={val.receiverAccountNo}
                transferAmt={val.transferAmt}
                senderContactNo={val.senderContactNo}
                message={val.message}
                createdAt={val.createdAt}
                invoiceCallback={generateInvoice}
                deleteCallback={deleteTransac}
              />
            );
          })}
        </tbody>
      </table>
      {/* {isUpdateModal ? (
        <UpdateUserModal
          show={isUpdateModal}
          onHide={() => setIsUpdateModal(false)}
          accountno={getProps.accountNo}
        />
      ) : null}
      {isDeleteModal ? (
        <DeleteUserModal
          show={isDeleteModal}
          onHide={() => setIsDeleteModal(false)}
          accountno={getProps.accountNo}
        />
      ) : null}
      {isSendMoneyModal ? (
        <SendMoneyModal
          show={isSendMoneyModal}
          onHide={() => setIsSendMoneyModal(false)}
          accountno={getProps.accountNo}
          name={getProps.name}
          balance={getProps.balance}
        />
      ) : null} */}
    </div>
  );
}

export default TransacTable;
