import React, { useEffect, useState } from "react";
import Axios from "axios";

import TableRow from "./TableRow";
import UpdateUserModal from "./UpdateUserModal";
import DeleteUserModal from "./DeleteUserModal";
import SendMoneyModal from "./SendMoneyModal";

function Table() {
  const [state, setState] = useState([]);
  const [isUpdateModal, setIsUpdateModal] = useState(false);
  const [isDeleteModal, setIsDeleteModal] = useState(false);
  const [isSendMoneyModal, setIsSendMoneyModal] = useState(false);
  const [getProps, setGetProps] = useState({
    accountNo: "",
    name: "",
    balance: 0,
  });

  const updateUser = (accountNo) => {
    setIsUpdateModal(true);
    setGetProps((prevState) => {
      return {
        ...prevState,
        accountNo,
      };
    });
  };

  const deleteUser = (accountNo) => {
    setIsDeleteModal(true);
    setGetProps((prevState) => {
      return {
        ...prevState,
        accountNo,
      };
    });
  };

  const sendMoney = (accountNo, name) => {
    setIsSendMoneyModal(true);
    setGetProps({
      accountNo,
      name,
      balance,
    });
  };

  useEffect(() => {
    Axios.get("http://localhost:5000/allUsers")
      .then((response) => setState(response.data))
      .catch((e) => {
        console.log("Can't catch the response...", e);
      });
  }, []);

  return (
    <div className="table-responsive-xl">
      <table className="table">
        <thead className="thead-dark">
          <tr>
            <>
              <th scope="col" className="text-center">
                Sr.No
              </th>
              <th scope="col" className="text-center">
                Account No
              </th>
              <th scope="col" className="text-center">
                Name
              </th>
              <th scope="col" className="text-center">
                Email Id
              </th>
              <th scope="col" className="text-center">
                Balance
              </th>
              <th scope="col" className="text-center">
                Contact No
              </th>
              <th scope="col" className="text-center">
                Account Type
              </th>
            </>
            <th scope="col" className="text-center">
              Actions
            </th>
          </tr>
        </thead>
        <tbody>
          {state.map((val, key) => {
            return (
              <TableRow
                key={val._id}
                srNo={key + 1}
                accountNo={val.accountNo}
                name={val.name}
                email={val.email}
                balance={val.balance}
                contactNo={val.contactNo}
                accountType={val.accountType}
                updateCallback={updateUser}
                deleteCallback={deleteUser}
                sendMoneyCallback={sendMoney}
              />
            );
          })}
        </tbody>
      </table>
      {isUpdateModal ? (
        <UpdateUserModal
          show={isUpdateModal}
          onHide={() => setIsUpdateModal(false)}
          accountno={getProps.accountNo}
        />
      ) : null}
      {isDeleteModal ? (
        <DeleteUserModal
          show={isDeleteModal}
          onHide={() => setIsDeleteModal(false)}
          accountno={getProps.accountNo}
        />
      ) : null}
      {isSendMoneyModal ? (
        <SendMoneyModal
          show={isSendMoneyModal}
          onHide={() => setIsSendMoneyModal(false)}
          accountno={getProps.accountNo}
          name={getProps.name}
        />
      ) : null}
    </div>
  );
}

export default Table;
