import axios from "axios";
import React, { useEffect, useState } from "react";
import { Button, Modal } from "react-bootstrap";
import colors from "../../config/colors";

function InvoiceGen(props) {
  const [detail, setDetail] = useState({});
  const [error, setError] = useState({ isError: false, error: "" });

  useEffect(() => {
    axios
      .get("http://localhost:5000/searchTransac/" + props.id)
      .then((response) => {
        setDetail(response.data);
        setError({ isError: false, error: "" });
      })
      .catch((e) => setError({ isError: true, error: "Network Error." }));
  }, []);

  return (
    <Modal
      {...props}
      size="md"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title
          id="contained-modal-title-vcenter"
          className="w-100 text-center title"
        >
          Invoice
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <div>
          <div
            className="para"
            style={{ color: colors.dark1, fontSize: "1.3em" }}
          >
            <div className="title2" style={{ width: "100%" }}>
              <div style={{ flex: 1 }}>
                <label>Sender Account No :</label>
                <label>{detail.senderAccountNo}</label>
              </div>
            </div>
          </div>
        </div>
      </Modal.Body>
      <Modal.Footer>
        <Button
          onClick={props.onHide}
          className="btn"
          style={{ backgroundColor: colors.primary2 }}
        >
          Cancel
        </Button>
        <Button onClick={() => alert("invoice download")}>Download</Button>
      </Modal.Footer>
    </Modal>
  );
}

export default InvoiceGen;
