import React, { useState } from "react";
import { Modal, Button } from "react-bootstrap";

import colors from "../../config/colors";

function SendMoneyModal(props) {
  const [detail, setDetail] = useState({ amount: 0, message: "" });
  return (
    <Modal {...props} aria-labelledby="contained-modal-title-vcenter" centered>
      <Modal.Header closeButton>
        <Modal.Title
          id="contained-modal-title-vcenter"
          className="w-100 text-center title"
        >
          Send Money
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <div>
          <label
            htmlFor="account-no"
            className="title2"
            style={{ width: "35%" }}
          >
            Account No :
          </label>
          <input
            type="text"
            value={props.accountno}
            className="input-field"
            style={{ width: "60%" }}
            disabled
          />
          <label htmlFor="name" className="title2" style={{ width: "35%" }}>
            Full Name :
          </label>
          <input
            type="text"
            value={props.name}
            className="input-field"
            style={{ width: "60%" }}
            disabled
          />
          <label htmlFor="name" className="title2" style={{ width: "35%" }}>
            Amount :
          </label>
          <input
            type="text"
            value={detail.amount}
            placeholder="Amount to send"
            className="input-field"
            style={{ width: "60%" }}
            onChange={(event) =>
              setDetail((prevState) => {
                return { ...prevState, amount: event.target.value };
              })
            }
          />
          <label htmlFor="name" className="title2" style={{ width: "35%" }}>
            Message :
          </label>
          <input
            type="text"
            value={detail.message}
            placeholder="Hi this is your birthday's gift."
            className="input-field"
            style={{ width: "60%" }}
            onChange={(event) =>
              setDetail((prevState) => {
                return { ...prevState, message: event.target.value };
              })
            }
          />
        </div>
      </Modal.Body>
      <Modal.Footer>
        <Button
          onClick={props.onHide}
          className="btn"
          style={{ backgroundColor: colors.primary2 }}
        >
          Cancel
        </Button>
        <Button>Send Money</Button>
      </Modal.Footer>
    </Modal>
  );
}

export default SendMoneyModal;
